public class Course extends CClass{		public String name=null;	public String c1;	public String c2;	public String c3;	public String c4;	public CClass class1;	Course()	{					}	Course(String n,CClass c)	{		name=n;		class1=c;	}	void add(String s1,String s2,String s3,String s4)	{		c1=s1;		c2=s2;		c3=s3;		c4=s4;	}	void dele(int i)	{		switch (i) {		case 1:			c1=null;			break ;		case 2:			c2=null;			break ;		case 3:			c3=null;			break ;		case 4:			c4=null;			break ;		default:			throw new IllegalArgumentException("Unexpected value: " + i);		}	}	void modifyname(String n)	{		name=n;			}	void modifyclass(CClass c)	{		class1=c;			}	void print()	{		System.out.println("Name:"+name+"  Class:"+class1.classnum);		if(c1!=null)			System.out.print(" course1:"+c1);		if(c2!=null)			System.out.print(" course2:"+c2);		if(c3!=null)			System.out.print(" course3:"+c3);		if(c4!=null)			System.out.print(" course4:"+c4);	}		}